  // Demo servers methods
  async getAllDemoServers(): Promise<DemoServer[]> {
    return await db
      .select()
      .from(demoServers)
      .where(eq(demoServers.isEnabled, true))
      .orderBy(demoServers.sortOrder);
  }

  async getDemoServer(id: string): Promise<DemoServer | undefined> {
