          return res.status(401).json({ message: "Invalid credentials" });
        }

        // Create admin session
        const token = randomUUID();
        const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000); // 24 hours

        await storage.createAdminSession({
          userId: user.id,
          token,
          expiresAt
        });

        res.json({ 
          token, 
