@tailwind base;
@tailwind components;
@tailwind utilities;

/* Performance optimizations */
* {
  box-sizing: border-box;
}

html {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
}

/* Optimize animations for performance */
@media (prefers-reduced-motion: no-preference) {
  .animate-spin {
    animation: spin 1s linear infinite;
  }

  .animate-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }
}

/* Disable animations for users who prefer reduced motion */
@media (prefers-reduced-motion: reduce) {
  *,
  ::before,
  ::after {
    animation-delay: -1ms !important;
    animation-duration: 1ms !important;
    animation-iteration-count: 1 !important;
    background-attachment: initial !important;
    scroll-behavior: auto !important;
    transition-duration: 0s !important;
    transition-delay: 0s !important;
  }
}

:root {
  --background: hsl(220, 13%, 6%);
  --foreground: hsl(213, 31%, 97%);
  --muted: hsl(220, 13%, 10%);
  --muted-foreground: hsl(215, 16%, 57%);
  --popover: hsl(220, 13%, 10%);
  --popover-foreground: hsl(213, 31%, 97%);
  --card: hsl(220, 13%, 10%);
  --card-foreground: hsl(213, 31%, 97%);
  --border: hsl(220, 13%, 15%);
  --input: hsl(220, 13%, 15%);
  --primary: hsl(147, 100%, 50%);
  --primary-foreground: hsl(220, 13%, 6%);
  --secondary: hsl(220, 13%, 15%);
  --secondary-foreground: hsl(213, 31%, 97%);
  --accent: hsl(220, 13%, 15%);
  --accent-foreground: hsl(213, 31%, 97%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(213, 31%, 97%);
  --ring: hsl(147, 100%, 50%);
  --radius: 0.5rem;

  /* Gaming theme colors */
  --gaming-green: hsl(147, 100%, 50%);
  --gaming-green-dark: hsl(147, 100%, 40%);
  --gaming-black: hsl(220, 13%, 6%);
  --gaming-black-light: hsl(220, 13%, 10%);
  --gaming-black-lighter: hsl(220, 13%, 15%);
  --gaming-gray: hsl(215, 16%, 57%);
  --gaming-white: hsl(213, 31%, 97%);
}

/* Ensure input text is visible in dark mode */
input[type="text"],
input[type="password"],
input[type="email"],
input[type="tel"],
textarea,
.bg-gaming-dark input,
.bg-gaming-dark-lighter input,
input.bg-gaming-dark,
input.bg-gaming-dark-lighter {
  color: white !important;
  background-color: hsl(220, 13%, 15%) !important;
}

input[type="text"]:focus,
input[type="password"]:focus,
input[type="email"]:focus,
input[type="tel"]:focus,
textarea:focus,
.bg-gaming-dark input:focus,
.bg-gaming-dark-lighter input:focus,
input.bg-gaming-dark:focus,
input.bg-gaming-dark-lighter:focus {
  color: white !important;
  background-color: hsl(220, 13%, 15%) !important;
}

input::placeholder,
textarea::placeholder {
  color: hsl(215, 16%, 57%) !important;
}

/* Force input styling for account settings */
.account-settings input {
  color: white !important;
  background-color: hsl(220, 13%, 15%) !important;
  border-color: hsl(142, 76%, 36%, 0.3) !important;
}

.light {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(147, 100%, 50%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(147, 100%, 50%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-gaming-black text-gaming-white;
    font-family: 'Inter', sans-serif;
  }
}

@layer utilities {
  .bg-gradient-gaming {
    background: linear-gradient(135deg, var(--gaming-black) 0%, var(--gaming-black-light) 50%, var(--gaming-black-lighter) 100%);
  }

  .bg-gradient-green {
    background: linear-gradient(135deg, var(--gaming-green) 0%, var(--gaming-green-dark) 100%);
  }

  .glass-effect {
    backdrop-filter: blur(10px);
    background: rgba(33, 38, 45, 0.3);
    border: 1px solid rgba(0, 255, 136, 0.1);
  }

  .text-gaming-green {
    color: var(--gaming-green);
  }

  .text-gaming-green-dark {
    color: var(--gaming-green-dark);
  }

  .text-gaming-black {
    color: var(--gaming-black);
  }

  .text-gaming-black-light {
    color: var(--gaming-black-light);
  }

  .text-gaming-black-lighter {
    color: var(--gaming-black-lighter);
  }

  .text-gaming-gray {
    color: var(--gaming-gray);
