@keyframes glow {
  0% { box-shadow: 0 0 5px var(--gaming-green), 0 0 10px var(--gaming-green), 0 0 15px var(--gaming-green); }
  100% { box-shadow: 0 0 10px var(--gaming-green), 0 0 20px var(--gaming-green), 0 0 30px var(--gaming-green); }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes pulse-green {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

.animate-glow {
  animation: glow 2s ease-in-out infinite alternate;
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-pulse-green {
  animation: pulse-green 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.server-online {
  animation: pulse-green 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Holiday theme animations */
@keyframes snowfall {
  0% { transform: translateY(-100vh) translateX(0px); }
  100% { transform: translateY(100vh) translateX(100px); }
}

@keyframes spooky-glow {
  0%, 100% { box-shadow: 0 0 5px #ff6600; }
  50% { box-shadow: 0 0 20px #ff6600, 0 0 30px #ff6600; }
}

@keyframes bounce-egg {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  25% { transform: translateY(-10px) rotate(-5deg); }
  50% { transform: translateY(-20px) rotate(0deg); }
  75% { transform: translateY(-10px) rotate(5deg); }
}

.snow-particle {
  position: fixed;
  top: -10px;
  color: white;
  font-size: 1em;
  animation: snowfall linear infinite;
  pointer-events: none;
  z-index: 1000;
}

.easter-egg {
