  background-color: hsl(220, 13%, 10%) !important;
  color: hsl(215, 16%, 47%) !important;
  border-color: hsl(217, 19%, 20%) !important;
  cursor: not-allowed;
  opacity: 0.6;
}

/* Client portal form fixes - ensure readability */
.client-portal form input,
.client-portal form textarea,
.client-portal form select,
.whmcs-form input,
.whmcs-form textarea,
.whmcs-form select {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
}

.client-portal form label,
.whmcs-form label {
  color: hsl(210, 40%, 98%) !important;
}

/* Contact form specific improvements */
.contact-form input,
.contact-form textarea,
.enhanced-contact input,
.enhanced-contact textarea {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
}

/* Newsletter and subscription forms */
.newsletter-form input,
.subscription-form input {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
}

/* Search forms */
.search-form input,
[role="search"] input,
input[type="search"] {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
}

/* Comprehensive select dropdown styling */
select,
.select-trigger,
[role="combobox"] {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
  font-size: 16px !important; /* Prevents zoom on mobile */
  min-height: 44px !important;
  padding: 0.75rem 2.5rem 0.75rem 1rem !important;
}

select:focus,
.select-trigger:focus,
[role="combobox"]:focus {
  border-color: var(--gaming-green) !important;
  box-shadow: 0 0 0 2px hsla(147, 100%, 50%, 0.2) !important;
  outline: none !important;
}

/* Select dropdown content styling */
.select-content,
[role="listbox"],
.dropdown-menu-content {
  background-color: hsl(220, 13%, 15%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
  color: hsl(210, 40%, 98%) !important;
  box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.5) !important;
}

.select-item,
[role="option"],
.dropdown-menu-item {
  background-color: transparent !important;
  color: hsl(210, 40%, 98%) !important;
  padding: 0.5rem 1rem !important;
  cursor: pointer;
}

.select-item:hover,
[role="option"]:hover,
.dropdown-menu-item:hover,
.select-item[data-highlighted],
[role="option"][data-highlighted] {
  background-color: var(--gaming-green) !important;
  color: hsl(220, 13%, 15%) !important;
}

/* Checkbox and radio button improvements */
input[type="checkbox"],
input[type="radio"] {
  width: 1.25rem !important;
  height: 1.25rem !important;
  accent-color: var(--gaming-green) !important;
  background-color: hsl(220, 13%, 15%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
}

/* File input styling */
input[type="file"] {
  background-color: hsl(220, 13%, 15%) !important;
  color: hsl(210, 40%, 98%) !important;
  border: 1px solid hsl(217, 19%, 27%) !important;
  padding: 0.75rem !important;
  border-radius: 0.375rem;
}

input[type="file"]::file-selector-button {
  background-color: var(--gaming-green) !important;
  color: hsl(220, 13%, 15%) !important;
  border: none !important;
  padding: 0.5rem 1rem !important;
  border-radius: 0.25rem;
  margin-right: 1rem;
  cursor: pointer;
}

/* Range/slider input styling */
input[type="range"] {
  background-color: hsl(217, 19%, 27%) !important;
  accent-color: var(--gaming-green) !important;
}

/* Number input spinner buttons */
input[type="number"]::-webkit-outer-spin-button,
input[type="number"]::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

input[type="number"] {
  -moz-appearance: textfield;
}

/* Better focus indicators for all interactive elements */
button:focus-visible,
a:focus-visible,
input:focus-visible,
textarea:focus-visible,
select:focus-visible,
[tabindex]:focus-visible {
  outline: 2px solid var(--gaming-green) !important;
  outline-offset: 2px !important;
}

/* Knowledge base tabs mobile optimization */
@media (max-width: 768px) {
  .tabs-list {
    flex-direction: column !important;
    width: 100% !important;
    height: auto !important;
  }

  .tabs-trigger {
    width: 100% !important;
    padding: 12px 16px !important;
    justify-content: flex-start !important;
    text-align: left !important;
    border-radius: 4px !important;
    margin-bottom: 2px !important;
  }

  .tabs-trigger span {
    font-size: 14px !important;
  }

  .tabs-trigger .lucide {
    margin-right: 8px !important;
    flex-shrink: 0 !important;
  }
}

/* Additional mobile optimizations for forms and buttons */
@media (max-width: 768px) {
  .mobile-full-width {
    width: 100% !important;
    min-width: 100% !important;
  }

  .mobile-stack {
    flex-direction: column !important;
    gap: 8px !important;
  }

  .mobile-text-sm {
    font-size: 14px !important;
  }

  .mobile-padding {
    padding: 12px 16px !important;
  }
}

