client/src/components/game-page-admin.ts.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  Plus, 
  Edit2, 
  Trash2, 
  Save,
  X,
  BookOpen,
  Tag
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import type { Game, BlogPost } from "@shared/schema";

interface GamePageCustomization {
  gameId: string;
  relatedArticles: string[];
  customSections: Array<{
    title: string;
    content: string;
    order: number;
  }>;
}

export default function GamePageAdmin() {
  const [selectedGame, setSelectedGame] = useState<Game | null>(null);
  const [customization, setCustomization] = useState<GamePageCustomization>({
    gameId: "",
    relatedArticles: [],
    customSections: []
  });
  const [newSection, setNewSection] = useState({ title: "", content: "" });
  const [showAddSection, setShowAddSection] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch games
  const { data: games = [] } = useQuery<Game[]>({
    queryKey: ["/api/games"],
    staleTime: 10 * 60 * 1000, // 10 minutes
    refetchOnWindowFocus: false,
  });

  // Fetch blog posts for related articles selection
  const { data: blogPosts = [] } = useQuery<BlogPost[]>({
    queryKey: ["/api/blog"],
  });

  // Fetch customization data for selected game
  const { data: gameCustomization } = useQuery({
    queryKey: ["/api/admin/game-pages", selectedGame?.id],
    queryFn: async () => {
      if (!selectedGame) return null;
      const response = await fetch(`/api/admin/game-pages/${selectedGame.id}`);
      if (!response.ok) return null;
      return response.json();
    },
    enabled: !!selectedGame,
  });

  // Update customization when data is loaded
  useEffect(() => {
    if (gameCustomization && selectedGame) {
      setCustomization({
        gameId: selectedGame.id,
        relatedArticles: gameCustomization.relatedArticles || [],
        customSections: gameCustomization.customSections || []
      });
    } else if (selectedGame) {
      setCustomization({
        gameId: selectedGame.id,
        relatedArticles: [],
        customSections: []
      });
    }
  }, [gameCustomization, selectedGame]);

  const saveCustomizationMutation = useMutation({
    mutationFn: (data: GamePageCustomization) => 
      apiRequest(`/api/admin/game-pages/${data.gameId}`, { 
        method: "PUT", 
        body: JSON.stringify(data) 
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/game-pages"] });
      toast({ title: "Game page customization saved successfully!" });
    },
    onError: (error: Error) => {
      toast({ 
        title: "Failed to save customization", 
        description: error.message, 
        variant: "destructive" 
      });
    },
  });

  const handleSaveCustomization = () => {
    if (!selectedGame) return;
    saveCustomizationMutation.mutate(customization);
  };

  const addRelatedArticle = (articleSlug: string) => {
    if (!customization.relatedArticles.includes(articleSlug)) {
      setCustomization(prev => ({
        ...prev,
        relatedArticles: [...prev.relatedArticles, articleSlug]
      }));
    }
  };

  const removeRelatedArticle = (articleSlug: string) => {
    setCustomization(prev => ({
      ...prev,
      relatedArticles: prev.relatedArticles.filter(slug => slug !== articleSlug)
    }));
  };

  const addCustomSection = () => {
    if (!newSection.title || !newSection.content) return;

    setCustomization(prev => ({
      ...prev,
      customSections: [...prev.customSections, {
        ...newSection,
        order: prev.customSections.length
      }]
    }));

    setNewSection({ title: "", content: "" });
    setShowAddSection(false);
  };

  const removeCustomSection = (index: number) => {
    setCustomization(prev => ({
      ...prev,
      customSections: prev.customSections.filter((_, i) => i !== index)
    }));
  };

  const availableArticles = blogPosts.filter(post => 
    post.isPublished && !customization.relatedArticles.includes(post.slug)
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gaming-white">Game Page Customization</h2>
      </div>

      {/* Game Selection */}
      <Card className="bg-gaming-black-lighter border-gaming-black-lighter">
        <CardHeader>
          <CardTitle className="text-gaming-white">Select Game to Customize</CardTitle>
          <CardDescription className="text-gaming-gray">
            Choose a game to customize its page content and related articles
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {games.map((game) => (
              <Card 
                key={game.id}
                className={`cursor-pointer transition-all ${
                  selectedGame?.id === game.id 
                    ? 'border-gaming-green bg-gaming-green/10' 
                    : 'border-gaming-black-light hover:border-gaming-green/50'
                }`}
                onClick={() => setSelectedGame(game)}
              >
                <CardContent className="p-4">
                  <img 
                    src={game.imageUrl} 
                    alt={game.name}
                    className="w-full h-20 object-cover rounded mb-2"
                  />
                  <h3 className="text-gaming-white font-semibold text-sm">{game.name}</h3>
                </CardContent>
              </Card>
            ))}
          </div>
        </CardContent>
      </Card>

      {selectedGame && (
        <>
          {/* Related Articles Management */}
          <Card className="bg-gaming-black-lighter border-gaming-black-lighter">
            <CardHeader>
              <CardTitle className="text-gaming-white flex items-center gap-2">
                <BookOpen className="w-5 h-5 text-gaming-green" />
                Related Articles for {selectedGame.name}
              </CardTitle>
              <CardDescription className="text-gaming-gray">
                Select blog articles to display on the {selectedGame.name} game page
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Currently Selected Articles */}
              {customization.relatedArticles.length > 0 && (
                <div>
                  <Label className="text-gaming-white mb-2 block">Selected Articles</Label>
                  <div className="flex flex-wrap gap-2">
                    {customization.relatedArticles.map((slug) => {
                      const article = blogPosts.find(post => post.slug === slug);
                      return article ? (
                        <Badge 
                          key={slug}
                          className="bg-gaming-green text-gaming-black flex items-center gap-1"
                        >
                          {article.title}
                          <X 
                            className="w-3 h-3 cursor-pointer"
                            onClick={() => removeRelatedArticle(slug)}
                          />
                        </Badge>
                      ) : null;
                    })}
                  </div>
                </div>
              )}

              {/* Available Articles */}
              {availableArticles.length > 0 && (
                <div>
                  <Label className="text-gaming-white mb-2 block">Available Articles</Label>
                  <div className="grid md:grid-cols-2 gap-2">
                    {availableArticles.slice(0, 6).map((article) => (
                      <Card 
                        key={article.id}
                        className="bg-gaming-black border-gaming-black-light hover:border-gaming-green/50 cursor-pointer"
                        onClick={() => addRelatedArticle(article.slug)}
                      >
                        <CardContent className="p-3">
                          <div className="flex items-center gap-2">
                            <div className="w-8 h-8 bg-gaming-green/20 rounded flex items-center justify-center">
                              <BookOpen className="w-4 h-4 text-gaming-green" />
                            </div>
                            <div className="flex-1 min-w-0">
                              <h4 className="text-gaming-white text-sm font-medium truncate">
                                {article.title}
                              </h4>
                              <p className="text-gaming-gray text-xs truncate">
                                {article.tags.join(", ")}
                              </p>
                            </div>
                            <Plus className="w-4 h-4 text-gaming-green" />
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Custom Sections Management */}
          <Card className="bg-gaming-black-lighter border-gaming-black-lighter">
            <CardHeader className="flex flex-row items-center justify-between">
              <div>
                <CardTitle className="text-gaming-white">Custom Sections</CardTitle>
                <CardDescription className="text-gaming-gray">
                  Add custom content sections to the {selectedGame.name} page
                </CardDescription>
              </div>
              <Button
                onClick={() => setShowAddSection(true)}
                className="bg-gaming-green hover:bg-gaming-green-dark text-gaming-black"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Section
              </Button>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Existing Custom Sections */}
              {customization.customSections.map((section, index) => (
                <Card key={index} className="bg-gaming-black border-gaming-black-light">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="text-gaming-white font-semibold">{section.title}</h4>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => removeCustomSection(index)}
                        className="text-red-500 hover:text-red-400"
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                    <p className="text-gaming-gray text-sm">{section.content}</p>
                  </CardContent>
                </Card>
              ))}

              {/* Add New Section Form */}
              {showAddSection && (
                <Card className="bg-gaming-black border-gaming-green">
                  <CardContent className="p-4 space-y-4">
                    <div className="flex items-center justify-between">
                      <h4 className="text-gaming-white font-semibold">Add New Section</h4>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setShowAddSection(false)}
                        className="text-gaming-gray"
                      >
                        <X className="w-4 h-4" />
                      </Button>
                    </div>
                    <div className="space-y-3">
                      <div>
                        <Label htmlFor="section-title" className="text-gaming-white">Section Title</Label>
                        <Input
                          id="section-title"
                          value={newSection.title}
                          onChange={(e) => setNewSection(prev => ({ ...prev, title: e.target.value }))}
                          placeholder="Enter section title"
                          className="bg-gaming-black-light border-gaming-black-light text-gaming-white"
                        />
                      </div>
                      <div>
                        <Label htmlFor="section-content" className="text-gaming-white">Section Content</Label>
                        <Textarea
                          id="section-content"
                          value={newSection.content}
                          onChange={(e) => setNewSection(prev => ({ ...prev, content: e.target.value }))}
                          placeholder="Enter section content"
                          rows={4}
                          className="bg-gaming-black-light border-gaming-black-light text-gaming-white"
                        />
                      </div>
                      <div className="flex gap-2">
                        <Button
                          onClick={addCustomSection}
                          className="bg-gaming-green hover:bg-gaming-green-dark text-gaming-black"
                        >
                          <Save className="w-4 h-4 mr-2" />
                          Add Section
                        </Button>
                        <Button
                          variant="outline"
                          onClick={() => setShowAddSection(false)}
                          className="border-gaming-gray text-gaming-gray"
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </CardContent>
          </Card>

          {/* Save Changes */}
          <div className="flex justify-center">
            <Button
              onClick={handleSaveCustomization}
              disabled={saveCustomizationMutation.isPending}
              className="bg-gaming-green hover:bg-gaming-green-dark text-gaming-black font-semibold px-8"
            >
              <Save className="w-4 h-4 mr-2" />
              {saveCustomizationMutation.isPending ? "Saving..." : "Save All Changes"}
            </Button>
          </div>
        </>
      )}
    </div>
  );
}

client/src/components/minecraft-tools.ts.content:
import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Search, 
  User, 
  Hash, 
  Palette, 
  Copy,
  Check,
  Server,
  Activity,
  Clock,
  Users,
  Wifi,
  AlertCircle,
  CheckCircle,
  Download,
  RefreshCw,
  Eye,
  Code
} from "lucide-react";

interface ServerStatus {
  online: boolean;
  players?: {
    current: number;
    max: number;
  };
  version?: string;
  ping?: number;
  motd?: string;
}

export default function MinecraftTools() {
  // Server Query Tool State
  const [serverAddress, setServerAddress] = useState("");
  const [serverPort, setServerPort] = useState("25565");
  const [serverStatus, setServerStatus] = useState<ServerStatus | null>(null);
  const [isQuerying, setIsQuerying] = useState(false);

  // UUID Converter State
  const [username, setUsername] = useState("");
  const [uuid, setUuid] = useState("");
  const [playerData, setPlayerData] = useState<any>(null);
  const [isLoadingPlayer, setIsLoadingPlayer] = useState(false);

  // Skin Viewer State
  const [skinUsername, setSkinUsername] = useState("");
  const [skinUrl, setSkinUrl] = useState("");

  // Color Code Generator State
  const [colorText, setColorText] = useState("");
  const [selectedColor, setSelectedColor] = useState("Â§a");
  const [generatedCode, setGeneratedCode] = useState("");

  // Copy State
  const [copied, setCopied] = useState<string | null>(null);

  const handleCopy = async (text: string, id: string) => {
    await navigator.clipboard.writeText(text);
    setCopied(id);
    setTimeout(() => setCopied(null), 2000);
  };

  const queryServer = async () => {
    if (!serverAddress) return;

    setIsQuerying(true);
    try {
      const response = await fetch(`/api/query-server/${serverAddress}/${serverPort}`);
      const data = await response.json();
      setServerStatus(data);
    } catch (error) {
      console.error("Failed to query server:", error);
      setServerStatus({ online: false });
    } finally {
      setIsQuerying(false);
    }
  };

  const lookupPlayer = async () => {
    if (!username) return;

    setIsLoadingPlayer(true);
    try {
      // Using Mojang API to get UUID
      const response = await fetch(`https://api.mojang.com/users/profiles/minecraft/${username}`);
      if (response.ok) {
        const data = await response.json();
        setPlayerData(data);
        setUuid(data.id);
      } else {
        setPlayerData(null);
        setUuid("");
      }
    } catch (error) {
      console.error("Failed to lookup player:", error);
      setPlayerData(null);
      setUuid("");
    } finally {
      setIsLoadingPlayer(false);
    }
  };

  const generateSkinUrl = () => {
    if (!skinUsername) return;
    const url = `https://mc-heads.net/body/${skinUsername}/256`;
    setSkinUrl(url);
  };

  const generateColorCode = () => {
    if (!colorText) return;
    const formatted = `${selectedColor}${colorText}`;
    setGeneratedCode(formatted);
  };

  const minecraftColors = [
    { code: "Â§0", name: "Black", color: "#000000" },
    { code: "Â§1", name: "Dark Blue", color: "#0000AA" },
    { code: "Â§2", name: "Dark Green", color: "#00AA00" },
    { code: "Â§3", name: "Dark Cyan", color: "#00AAAA" },
    { code: "Â§4", name: "Dark Red", color: "#AA0000" },
    { code: "Â§5", name: "Purple", color: "#AA00AA" },
    { code: "Â§6", name: "Gold", color: "#FFAA00" },
    { code: "Â§7", name: "Gray", color: "#AAAAAA" },
    { code: "Â§8", name: "Dark Gray", color: "#555555" },
    { code: "Â§9", name: "Blue", color: "#5555FF" },
    { code: "Â§a", name: "Green", color: "#55FF55" },
    { code: "Â§b", name: "Cyan", color: "#55FFFF" },
    { code: "Â§c", name: "Red", color: "#FF5555" },
    { code: "Â§d", name: "Pink", color: "#FF55FF" },
    { code: "Â§e", name: "Yellow", color: "#FFFF55" },
    { code: "Â§f", name: "White", color: "#FFFFFF" }
  ];

  return (
    <section id="tools" className="py-20 bg-gaming-black">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-4xl lg:text-5xl font-bold mb-6">
            <span className="text-gaming-green">MC</span> Tools
          </h2>
          <p className="text-xl text-gaming-gray max-w-3xl mx-auto">
            Professional Minecraft server management and player tools with real-time data and API integration.
          </p>
        </div>

        <Tabs defaultValue="server-query" className="w-full">
          <TabsList className="grid w-full grid-cols-2 lg:grid-cols-4 mb-8">
            <TabsTrigger value="server-query" className="flex items-center gap-2">
              <Server className="h-4 w-4" />
              Server Query
            </TabsTrigger>
            <TabsTrigger value="player-lookup" className="flex items-center gap-2">
              <User className="h-4 w-4" />
              Player Lookup
            </TabsTrigger>
            <TabsTrigger value="skin-viewer" className="flex items-center gap-2">
              <Eye className="h-4 w-4" />
              Skin Viewer
            </TabsTrigger>
            <TabsTrigger value="color-codes" className="flex items-center gap-2">
              <Palette className="h-4 w-4" />
              Color Codes
            </TabsTrigger>
          </TabsList>

          <TabsContent value="server-query">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Server className="h-5 w-5 text-gaming-green" />
                  Server Query Tool
                </CardTitle>
                <CardDescription>
                  Query any Minecraft server for real-time status, player count, and server information
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="server-address">Server Address</Label>
                      <Input
                        id="server-address"
                        placeholder="mc.hypixel.net"
                        value={serverAddress}
                        onChange={(e) => setServerAddress(e.target.value)}
                      />
                    </div>
                    <div>
                      <Label htmlFor="server-port">Port</Label>
                      <Input
                        id="server-port"
                        placeholder="25565"
                        value={serverPort}
                        onChange={(e) => setServerPort(e.target.value)}
                      />
                    </div>
                    <Button 
                      onClick={queryServer}
                      disabled={isQuerying || !serverAddress}
                      className="w-full bg-gaming-green text-gaming-black hover:shadow-lg"
                    >
                      {isQuerying ? (
                        <>
                          <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                          Querying...
                        </>
                      ) : (
                        <>
                          <Search className="h-4 w-4 mr-2" />
                          Query Server
                        </>
                      )}
                    </Button>
                  </div>

                  {serverStatus && (
                    <div className="space-y-4">
                      <div className="bg-gaming-black-lighter p-4 rounded-lg">
                        <div className="flex items-center gap-2 mb-3">
                          {serverStatus.online ? (
                            <CheckCircle className="h-5 w-5 text-gaming-green" />
                          ) : (
                            <AlertCircle className="h-5 w-5 text-red-500" />
                          )}
                          <span className="font-semibold text-gaming-white">
                            {serverStatus.online ? "Online" : "Offline"}
                          </span>
                        </div>

                        {serverStatus.online && (
                          <div className="space-y-2 text-sm">
                            {serverStatus.players && (
                              <div className="flex items-center gap-2">
                                <Users className="h-4 w-4 text-gaming-green" />
                                <span className="text-gaming-gray">
                                  Players: {serverStatus.players.current}/{serverStatus.players.max}
                                </span>
                              </div>
                            )}
                            {serverStatus.version && (
                              <div className="flex items-center gap-2">
                                <Activity className="h-4 w-4 text-gaming-green" />
                                <span className="text-gaming-gray">Version: {serverStatus.version}</span>
                              </div>
                            )}
                            {serverStatus.ping && (
                              <div className="flex items-center gap-2">
                                <Wifi className="h-4 w-4 text-gaming-green" />
                                <span className="text-gaming-gray">Ping: {serverStatus.ping}ms</span>
                              </div>
                            )}
                            {serverStatus.motd && (
                              <div className="mt-3">
                                <p className="text-xs text-gaming-gray mb-1">MOTD:</p>
                                <p className="text-gaming-white text-sm font-mono bg-gaming-black p-2 rounded">
                                  {serverStatus.motd}
                                </p>
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="player-lookup">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <User className="h-5 w-5 text-gaming-green" />
                  Player UUID Lookup
                </CardTitle>
                <CardDescription>
                  Convert Minecraft usernames to UUIDs and get player information
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="username">Minecraft Username</Label>
                      <Input
                        id="username"
                        placeholder="Notch"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </div>
                    <Button 
                      onClick={lookupPlayer}
                      disabled={isLoadingPlayer || !username}
                      className="w-full bg-gaming-green text-gaming-black hover:shadow-lg"
                    >
                      {isLoadingPlayer ? (
                        <>
                          <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                          Looking up...
                        </>
                      ) : (
                        <>
                          <Search className="h-4 w-4 mr-2" />
                          Lookup Player
                        </>
                      )}
                    </Button>
                  </div>

                  {playerData && (
                    <div className="space-y-4">
                      <div className="bg-gaming-black-lighter p-4 rounded-lg">
                        <h4 className="font-semibold text-gaming-white mb-3">Player Information</h4>
                        <div className="space-y-2">
                          <div>
                            <Label className="text-gaming-gray text-xs">Username</Label>
                            <div className="flex items-center gap-2">
                              <code className="text-gaming-white bg-gaming-black px-2 py-1 rounded text-sm">
                                {playerData.name}
                              </code>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleCopy(playerData.name, 'username')}
                              >
                                {copied === 'username' ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
                              </Button>
                            </div>
                          </div>
                          <div>
                            <Label className="text-gaming-gray text-xs">UUID</Label>
                            <div className="flex items-center gap-2">
                              <code className="text-gaming-white bg-gaming-black px-2 py-1 rounded text-sm">
                                {uuid}
                              </code>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleCopy(uuid, 'uuid')}
                              >
                                {copied === 'uuid' ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
                              </Button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="skin-viewer">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Eye className="h-5 w-5 text-gaming-green" />
                  Minecraft Skin Viewer
                </CardTitle>
                <CardDescription>
                  View and download Minecraft player skins in high resolution
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="space-y-4">
                    <div>
                      <Label htmlFor="skin-username">Minecraft Username</Label>
                      <Input
                        id="skin-username"
                        placeholder="Steve"
                        value={skinUsername}
                        onChange={(e) => setSkinUsername(e.target.value)}
                      />
                    </div>
                    <Button 
                      onClick={generateSkinUrl}
                      disabled={!skinUsername}
                      className="w-full bg-gaming-green text-gaming-black hover:shadow-lg"
                    >
                      <Eye className="h-4 w-4 mr-2" />
                      View Skin
                    </Button>
                  </div>

                  {skinUrl && (
                    <div className="flex flex-col items-center space-y-4">
                      <div className="bg-gaming-black-lighter p-4 rounded-lg">
                        <img 
                          src={skinUrl} 
                          alt={`${skinUsername}'s skin`}
                          className="w-32 h-64 pixelated"
                          style={{ imageRendering: 'pixelated' }}
                        />
                      </div>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => window.open(skinUrl, '_blank')}
                        >
                          <Download className="h-3 w-3 mr-1" />
                          Download
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleCopy(skinUrl, 'skin-url')}
                        >
                          {copied === 'skin-url' ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="color-codes">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Palette className="h-5 w-5 text-gaming-green" />
                  Minecraft Color Code Generator
                </CardTitle>
                <CardDescription>
                  Generate formatted text with Minecraft color codes for signs, books, and chat
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-6">
                  <div className="grid md:grid-cols-2 gap-6">
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="color-text">Text to Format</Label>
                        <Input
                          id="color-text"
                          placeholder="Enter your text here"
                          value={colorText}
                          onChange={(e) => setColorText(e.target.value)}
                        />
                      </div>

                      <div>
                        <Label>Select Color</Label>
                        <div className="grid grid-cols-4 gap-2 mt-2">
                          {minecraftColors.map((color) => (
                            <button
                              key={color.code}
                              onClick={() => setSelectedColor(color.code)}
                              className={`p-2 rounded border text-xs ${
                                selectedColor === color.code 
                                  ? 'border-gaming-green bg-gaming-green/10' 
                                  : 'border-gaming-black-lighter bg-gaming-black-lighter'
                              }`}
                              style={{ color: color.color }}
                            >
                              {color.name}
                            </button>
                          ))}
                        </div>
                      </div>

                      <Button 
                        onClick={generateColorCode}
                        disabled={!colorText}
                        className="w-full bg-gaming-green text-gaming-black hover:shadow-lg"
                      >
                        <Code className="h-4 w-4 mr-2" />
                        Generate Code
                      </Button>
                    </div>

                    {generatedCode && (
                      <div className="space-y-4">
                        <div>
                          <Label>Generated Code</Label>
                          <div className="flex items-center gap-2 mt-2">
                            <code className="flex-1 text-gaming-white bg-gaming-black p-3 rounded text-sm">
                              {generatedCode}
                            </code>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleCopy(generatedCode, 'color-code')}
                            >
                              {copied === 'color-code' ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
                            </Button>
                          </div>
                        </div>

                        <div>
                          <Label>Preview</Label>
                          <div className="bg-gaming-black p-3 rounded mt-2">
                            <span style={{ color: minecraftColors.find(c => c.code === selectedColor)?.color }}>
                              {colorText}
                            </span>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </section>
  );
}

client/src/components/navigation.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Menu, X, Zap } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";

interface ThemeSettings {
  siteName?: string;
  logoUrl?: string;
}

export default function Navigation() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [location] = useLocation();

