            <div className="space-y-6">
              {/* Header */}
              <div className="flex items-center justify-between">
                <div>
                  <h2 className="text-2xl font-bold text-gaming-white mb-2">Status & Incidents Management</h2>
                  <p className="text-gaming-gray">Manage service status and incident reports for your status page</p>
                </div>
                <Button 
                  onClick={handleMobileClick(() => setIsCreateIncidentOpen(true))}
                  onTouchStart={handleMobileTouchStart}
                  className="bg-gaming-green text-black hover:bg-gaming-green/90 touch-target"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  New Incident
                </Button>
              </div>

              {/* Current Incidents */}
              <Card className="bg-gaming-black-light border-gaming-green/30">
                <CardHeader>
                  <CardTitle className="text-gaming-white">Active Incidents</CardTitle>
                  <CardDescription className="text-gaming-gray">
                    Current ongoing service issues
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {incidents.filter((incident: any) => !incident.isResolved).length === 0 ? (
                      <div className="text-center py-8">
                        <div className="w-16 h-16 mx-auto mb-4 bg-gaming-green/20 rounded-full flex items-center justify-center">
                          <Zap className="w-8 h-8 text-gaming-green" />
                        </div>
                        <p className="text-gaming-gray mb-2">All systems operational</p>
                        <p className="text-sm text-gaming-gray/70">No active incidents to display</p>
                      </div>
                    ) : (
                      incidents.filter((incident: any) => !incident.isResolved).map((incident: any) => (
                        <div key={incident.id} className="border border-gaming-green/30 rounded-lg p-4">
                          <div className="flex items-center justify-between mb-2">
                            <h3 className="text-gaming-white font-semibold">{incident.title}</h3>
                            <div className="flex items-center gap-2">
                              <Badge variant={incident.severity === 'critical' ? 'destructive' : incident.severity === 'major' ? 'secondary' : 'default'}>
                                {incident.severity}
                              </Badge>
                              <Badge variant={incident.status === 'resolved' ? 'default' : 'secondary'}>
                                {incident.status}
                              </Badge>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => editIncident(incident)}
                                className="text-gaming-green hover:text-gaming-green/80"
                              >
                                <Edit2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                          <p className="text-gaming-gray text-sm mb-2">{incident.description}</p>
                          <div className="flex items-center gap-4 text-xs text-gaming-gray/70">
                            <span>Started: {new Date(incident.startTime).toLocaleString()}</span>
                            {incident.affectedServices?.length > 0 && (
                              <span>Services: {incident.affectedServices.join(', ')}</span>
                            )}
                          </div>
                        </div>
                      ))
                    )}
                  </div>
                </CardContent>
              </Card>

              {/* Recent Resolved Incidents */}
              <Card className="bg-gaming-black-light border-gaming-green/30">
                <CardHeader>
                  <CardTitle className="text-gaming-white">Recent Resolved Incidents</CardTitle>
                  <CardDescription className="text-gaming-gray">
                    Previously resolved issues (last 30 days)
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {incidents.filter((incident: any) => incident.isResolved).length === 0 ? (
                      <div className="text-center py-4">
                        <p className="text-gaming-gray text-sm">No recent incidents</p>
                      </div>
                    ) : (
                      incidents.filter((incident: any) => incident.isResolved).slice(0, 5).map((incident: any) => (
                        <div key={incident.id} className="border border-gaming-green/20 rounded-lg p-3">
                          <div className="flex items-center justify-between mb-1">
                            <h4 className="text-gaming-white text-sm font-medium">{incident.title}</h4>
                            <div className="flex items-center gap-2">
                              <Badge variant="outline" className="text-xs">
                                {incident.severity}
                              </Badge>
                              <Badge variant="default" className="text-xs">
                                resolved
                              </Badge>
                            </div>
                          </div>
                          <p className="text-gaming-gray text-xs mb-2">{incident.description}</p>
                          <div className="flex items-center gap-4 text-xs text-gaming-gray/50">
                            <span>Duration: {incident.endTime ? 
                              Math.round((new Date(incident.endTime) - new Date(incident.startTime)) / (1000 * 60)) + ' minutes' 
                              : 'Unknown'}</span>
                            <span>Resolved: {incident.endTime ? new Date(incident.endTime).toLocaleString() : 'N/A'}</span>
                          </div>
                        </div>
                      ))
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
        </Tabs>
      </div>

      {/* Incident Creation/Edit Dialog */}
      <Dialog open={isCreateIncidentOpen} onOpenChange={setIsCreateIncidentOpen}>
        <DialogContent className="bg-gaming-black-light border-gaming-green/30 text-gaming-white max-w-2xl">
          <DialogHeader>
            <DialogTitle className="text-gaming-white">
              {editingIncident ? 'Edit Incident' : 'Create New Incident'}
            </DialogTitle>
            <DialogDescription className="text-gaming-gray">
              {editingIncident ? 'Update incident details and status' : 'Report a new service incident or outage'}
            </DialogDescription>
          </DialogHeader>

          <div className={`space-y-4 ${isMobile ? 'admin-mobile-form' : ''}`}>
            <div className="form-group">
              <Label className="text-gaming-white">Incident Title</Label>
              <Input
                value={incidentForm.title}
                onChange={(e) => setIncidentForm({...incidentForm, title: e.target.value})}
                placeholder="Brief description of the issue"
                className="bg-gaming-black border-gaming-green/30 text-gaming-white"
              />
            </div>

            <div className="form-group">
              <Label className="text-gaming-white">Description</Label>
              <Textarea
                value={incidentForm.description}
                onChange={(e) => setIncidentForm({...incidentForm, description: e.target.value})}
                placeholder="Detailed description of the incident and its impact"
                rows={3}
                className="bg-gaming-black border-gaming-green/30 text-gaming-white"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="form-group">
                <Label className="text-gaming-white">Severity</Label>
                <Select value={incidentForm.severity} onValueChange={(value) => setIncidentForm({...incidentForm, severity: value})}>
                  <SelectTrigger className="bg-gaming-black border-gaming-green/30 text-gaming-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-gaming-black border-gaming-green/30">
                    <SelectItem value="minor">Minor</SelectItem>
                    <SelectItem value="major">Major</SelectItem>
                    <SelectItem value="critical">Critical</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="form-group">
                <Label className="text-gaming-white">Status</Label>
                <Select value={incidentForm.status} onValueChange={(value) => setIncidentForm({...incidentForm, status: value})}>
                  <SelectTrigger className="bg-gaming-black border-gaming-green/30 text-gaming-white">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-gaming-black border-gaming-green/30">
                    <SelectItem value="investigating">Investigating</SelectItem>
                    <SelectItem value="identified">Identified</SelectItem>
                    <SelectItem value="monitoring">Monitoring</SelectItem>
                    <SelectItem value="resolved">Resolved</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="form-group">
              <Label className="text-gaming-white">Status Updates (Optional)</Label>
              <Textarea
                value={incidentForm.updates}
                onChange={(e) => setIncidentForm({...incidentForm, updates: e.target.value})}
                placeholder="Additional updates or progress information"
                rows={2}
                className="bg-gaming-black border-gaming-green/30 text-gaming-white"
              />
            </div>
          </div>

          <DialogFooter className={isMobile ? 'admin-mobile-buttons' : 'flex gap-2'}>
            <Button
              variant="outline"
              onClick={() => {
                setIsCreateIncidentOpen(false);
                setEditingIncident(null);
                setIncidentForm({
                  title: "",
                  description: "",
                  severity: "minor",
                  status: "investigating",
                  affectedServices: [],
                  updates: ""
                });
              }}
              className="border-gaming-green/30 text-gaming-white hover:bg-gaming-green/10"
            >
              Cancel
            </Button>
            <Button
              onClick={handleSubmitIncident}
              disabled={createIncidentMutation.isPending || updateIncidentMutation.isPending}
              className="bg-gaming-green text-black hover:bg-gaming-green/90"
            >
              {(createIncidentMutation.isPending || updateIncidentMutation.isPending) ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  {editingIncident ? 'Updating...' : 'Creating...'}
                </>
              ) : (
                editingIncident ? 'Update Incident' : 'Create Incident'
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      <Footer />
    </div>
  );
}

