                  onChange={(e) => updateStyle({ backgroundColor: e.target.value })}
                  className="w-12 h-10 rounded border-gaming-green/30"
                />
                <Input
                  value={section.style.backgroundColor}
                  onChange={(e) => updateStyle({ backgroundColor: e.target.value })}
