import { useState, useMemo } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from '@/components/ui/select';
import { 
  Search, 
  Filter, 
  Users, 
  Star, 
  TrendingUp, 
  Sparkles,
  Server,
  Gamepad2,
  Zap,
  Shield,
  Clock
} from 'lucide-react';
import Navigation from '@/components/navigation';
import PromoBanner from '@/components/promo-banner';
import Footer from '@/components/footer';
import { Link } from 'wouter';

interface Game {
  id: string;
  name: string;
  slug: string;
  description: string;
  imageUrl: string;
  basePrice: string;
  playerCount: number;
  isPopular: boolean;
  isNew: boolean;
  isTrending: boolean;
  category?: string;
  tags?: string[];
  platform?: 'PC' | 'Console' | 'Crossplay';
  minPlayers?: number;
  maxPlayers?: number;
}

export default function GamesPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedPlatform, setSelectedPlatform] = useState('all');
  const [sortBy, setSortBy] = useState('popular');

  // Fetch games
  const { data: games = [], isLoading } = useQuery<Game[]>({
    queryKey: ['/api/games'],
    staleTime: 10 * 60 * 1000, // 10 minutes
    refetchOnWindowFocus: false,
    refetchOnMount: false,
  });

  // Game categories
  const categories = [
    { value: 'all', label: 'All Games', icon: <Gamepad2 className="w-4 h-4" /> },
    { value: 'survival', label: 'Survival', icon: <Shield className="w-4 h-4" /> },
    { value: 'action', label: 'Action', icon: <Zap className="w-4 h-4" /> },
    { value: 'simulation', label: 'Simulation', icon: <Server className="w-4 h-4" /> },
    { value: 'strategy', label: 'Strategy', icon: <Clock className="w-4 h-4" /> },
    { value: 'sandbox', label: 'Sandbox', icon: <Sparkles className="w-4 h-4" /> },
  ];

  // Enhanced games data with categories and tags
  const enhancedGames = useMemo(() => {
    return games.map(game => ({
      ...game,
      category: game.name === 'Minecraft' ? 'sandbox' :
                game.name === 'CS2' ? 'action' :
                game.name === 'Rust' ? 'survival' :
                game.name === 'ARK: Survival' ? 'survival' :
                game.name === 'Valheim' ? 'survival' :
                game.name === 'Satisfactory' ? 'simulation' :
                game.name === 'Factorio' ? 'simulation' :
                game.name === 'Squad' ? 'action' :
                'action',
      tags: game.name === 'Minecraft' ? ['Building', 'Creative', 'Multiplayer'] :
            game.name === 'CS2' ? ['Competitive', 'FPS', 'Team-based'] :
            game.name === 'Rust' ? ['PvP', 'Crafting', 'Base Building'] :
            game.name === 'ARK: Survival' ? ['Dinosaurs', 'Taming', 'PvE'] :
            game.name === 'Valheim' ? ['Co-op', 'Norse', 'Exploration'] :
            ['Multiplayer', 'Action'],
      platform: game.name === 'Minecraft' ? 'Crossplay' as const :
                  game.name === 'CS2' ? 'PC' as const :
                  game.name === 'Rust' ? 'PC' as const :
                  'PC' as const,
      minPlayers: 1,
      maxPlayers: game.name === 'Minecraft' ? 100 :
                  game.name === 'CS2' ? 32 :
                  game.name === 'Rust' ? 200 :
                  50
    }));
  }, [games]);

  // Filter and sort games
  const filteredAndSortedGames = useMemo(() => {
    let filtered = enhancedGames.filter(game => {
      const matchesSearch = game.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           game.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           (game.tags || []).some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));

      const matchesCategory = selectedCategory === 'all' || game.category === selectedCategory;
      const matchesPlatform = selectedPlatform === 'all' || game.platform === selectedPlatform;

      return matchesSearch && matchesCategory && matchesPlatform;
    });

    // Sort games
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'popular':
          return b.playerCount - a.playerCount;
        case 'name':
          return a.name.localeCompare(b.name);
        case 'price':
          return parseFloat(a.basePrice) - parseFloat(b.basePrice);
        case 'newest':
          return (b.isNew ? 1 : 0) - (a.isNew ? 1 : 0);
        default:
          return 0;
      }
    });

    return filtered;
  }, [enhancedGames, searchTerm, selectedCategory, selectedPlatform, sortBy]);

  const getPlatformColor = (platform: string) => {
    switch (platform) {
      case 'PC': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'Console': return 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200';
      case 'Mobile': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'Crossplay': return 'bg-gaming-green/20 text-gaming-green border border-gaming-green/30';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gaming-black">
        <PromoBanner />
        <Navigation />
        <div className="container mx-auto px-4 py-20">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-gaming-green"></div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gaming-black">
      <PromoBanner />
      <Navigation />

      {/* Hero Section */}
      <section className="py-20 bg-gradient-gaming">
        <div className="container mx-auto px-4">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-5xl font-bold text-gaming-white mb-6">
              Game <span className="text-gaming-green">Server</span> Hosting
            </h1>
            <p className="text-xl text-gaming-gray mb-8">
              Choose from our extensive library of games and deploy your server instantly
            </p>

            {/* Quick Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 max-w-2xl mx-auto">
              <div className="bg-gaming-black-lighter/50 rounded-lg p-4">
                <div className="text-2xl font-bold text-gaming-green">{games.length}+</div>
                <div className="text-sm text-gaming-gray">Games</div>
              </div>
              <div className="bg-gaming-black-lighter/50 rounded-lg p-4">
                <div className="text-2xl font-bold text-gaming-green">24/7</div>
                <div className="text-sm text-gaming-gray">Support</div>
              </div>
              <div className="bg-gaming-black-lighter/50 rounded-lg p-4">
                <div className="text-2xl font-bold text-gaming-green">99.9%</div>
                <div className="text-sm text-gaming-gray">Uptime</div>
              </div>
              <div className="bg-gaming-black-lighter/50 rounded-lg p-4">
                <div className="text-2xl font-bold text-gaming-green">Instant</div>
                <div className="text-sm text-gaming-gray">Setup</div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Search and Filters */}
      <section className="py-12 bg-gaming-black-light">
        <div className="container mx-auto px-4">
          <div className="bg-gaming-black-lighter rounded-xl p-6 border border-gaming-green/20">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
              {/* Search */}
              <div className="lg:col-span-2">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gaming-gray w-4 h-4" />
                  <Input
                    placeholder="Search games..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 bg-gaming-black border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray focus:border-gaming-green"
                  />
                </div>
              </div>

              {/* Category Filter */}
              <div>
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger className="bg-gaming-black border-gaming-green/30 text-gaming-white">
                    <SelectValue placeholder="Category" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((category) => (
                      <SelectItem key={category.value} value={category.value}>
                        <div className="flex items-center gap-2">
                          {category.icon}
                          {category.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Platform Filter */}
              <div>
                <Select value={selectedPlatform} onValueChange={setSelectedPlatform}>
                  <SelectTrigger className="bg-gaming-black border-gaming-green/30 text-gaming-white">
                    <SelectValue placeholder="Platform" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Platforms</SelectItem>
                    <SelectItem value="PC">PC</SelectItem>
                    <SelectItem value="Console">Console</SelectItem>
                    <SelectItem value="Mobile">Mobile</SelectItem>
                    <SelectItem value="Crossplay">Crossplay</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Sort */}
              <div>
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="bg-gaming-black border-gaming-green/30 text-gaming-white">
                    <SelectValue placeholder="Sort by" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="popular">Most Popular</SelectItem>
                    <SelectItem value="name">Name A-Z</SelectItem>
                    <SelectItem value="price">Price Low-High</SelectItem>
                    <SelectItem value="newest">Newest</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {/* Active Filters */}
            <div className="flex flex-wrap gap-2 mt-4">
              {searchTerm && (
                <Badge variant="secondary" className="bg-gaming-green/20 text-gaming-green">
                  Search: {searchTerm}
                  <button 
                    onClick={() => setSearchTerm('')}
                    className="ml-2 hover:text-gaming-white"
                  >
                    Ã—
                  </button>
                </Badge>
              )}
              {selectedCategory !== 'all' && (
                <Badge variant="secondary" className="bg-gaming-green/20 text-gaming-green">
                  Category: {categories.find(c => c.value === selectedCategory)?.label}
                  <button 
                    onClick={() => setSelectedCategory('all')}
                    className="ml-2 hover:text-gaming-white"
                  >
                    Ã—
                  </button>
                </Badge>
              )}
              {selectedPlatform !== 'all' && (
                <Badge variant="secondary" className="bg-gaming-green/20 text-gaming-green">
                  Platform: {selectedPlatform}
                  <button 
                    onClick={() => setSelectedPlatform('all')}
                    className="ml-2 hover:text-gaming-white"
                  >
                    Ã—
                  </button>
                </Badge>
              )}
            </div>

            {/* Results Count */}
            <div className="text-sm text-gaming-gray mt-4">
              Showing {filteredAndSortedGames.length} of {games.length} games
            </div>
          </div>
        </div>
      </section>

      {/* Games Grid */}
      <section className="py-16">
        <div className="container mx-auto px-4">
          {filteredAndSortedGames.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {filteredAndSortedGames.map((game) => (
                <Card 
                  key={game.id} 
                  className="bg-gaming-black-lighter border-gaming-green/20 hover:border-gaming-green/50 transition-all duration-300 group overflow-hidden"
                >
                  <div className="relative">
                    <img 
                      src={game.imageUrl} 
                      alt={game.name}
                      className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                    />

                    {/* Overlay badges */}
                    <div className="absolute top-4 left-4 flex flex-col gap-2">
                      {game.isPopular && (
                        <Badge className="bg-gaming-green text-gaming-black">
                          <Star className="w-3 h-3 mr-1" />
                          Popular
                        </Badge>
                      )}
                      {game.isNew && (
                        <Badge className="bg-blue-500 text-white">
                          <Sparkles className="w-3 h-3 mr-1" />
                          New
                        </Badge>
                      )}
                      {game.isTrending && (
                        <Badge className="bg-red-500 text-white">
                          <TrendingUp className="w-3 h-3 mr-1" />
                          Trending
                        </Badge>
                      )}
                    </div>

                    {/* Price badge */}
                    <div className="absolute top-4 right-4">
                      <Badge className="bg-gaming-green text-gaming-black font-bold text-lg px-3 py-1">
                        ${game.basePrice}/mo
                      </Badge>
                    </div>
                  </div>

                  <CardContent className="p-6">
                    <div className="flex items-center justify-between mb-2">
                      <h3 className="text-xl font-bold text-gaming-white group-hover:text-gaming-green transition-colors">
                        {game.name}
                      </h3>
                      <Badge className={getPlatformColor(game.platform || 'PC')}>
                        {game.platform}
                      </Badge>
                    </div>

                    <p className="text-gaming-gray text-sm mb-4 line-clamp-2">
                      {game.description}
                    </p>

                    {/* Tags */}
                    <div className="flex flex-wrap gap-1 mb-4">
                      {(game.tags || []).slice(0, 3).map((tag) => (
                        <Badge 
                          key={tag} 
                          variant="outline" 
                          className="text-xs border-gaming-green/30 text-gaming-gray"
                        >
                          {tag}
                        </Badge>
                      ))}
                    </div>

                    {/* Stats */}
                    <div className="flex items-center justify-between text-sm text-gaming-gray mb-4">
                      <div className="flex items-center gap-1">
                        <Users className="w-4 h-4" />
                        {game.playerCount.toLocaleString()} players
                      </div>
                      <div className="flex items-center gap-1">
                        <Server className="w-4 h-4" />
                        Max {game.maxPlayers}
                      </div>
                    </div>

                    {/* Action Button */}
                    <Link href={game.slug === 'minecraft' ? '/games/minecraft' : `/games/${game.slug}`}>
                      <Button className="w-full bg-gaming-green hover:bg-gaming-green-dark text-gaming-black font-semibold group-hover:shadow-lg group-hover:shadow-gaming-green/25 transition-all">
                        Deploy Server
                      </Button>
                    </Link>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <div className="w-24 h-24 bg-gaming-green/20 rounded-full flex items-center justify-center mx-auto mb-4">
                <Search className="w-12 h-12 text-gaming-green" />
              </div>
              <h3 className="text-xl font-semibold text-gaming-white mb-2">No games found</h3>
              <p className="text-gaming-gray mb-6">
                Try adjusting your search criteria or browse all games
              </p>
              <Button 
                onClick={() => {
                  setSearchTerm('');
                  setSelectedCategory('all');
                  setSelectedPlatform('all');
                }}
                className="bg-gaming-green hover:bg-gaming-green-dark text-gaming-black"
              >
                Clear All Filters
              </Button>
            </div>
          )}
        </div>
      </section>

      <Footer />
    </div>
  );
}

client/src/components/game-image.tsx.content:
import { useState } from 'react';

interface GameImageProps {
  src: string;
  alt: string;
  className?: string;
  loading?: 'lazy' | 'eager';
  gameSlug?: string;
  onError?: (e: React.SyntheticEvent<HTMLImageElement, Event>) => void;
}

// Map of game slugs to local SVG images
const LOCAL_GAME_IMAGES: Record<string, string> = {
  'minecraft': '/images/games/minecraft.svg',
  'minecraft-java': '/images/games/minecraft.svg',
  'rust': '/images/games/rust.svg',
  'cs2': '/images/games/cs2.svg',
  'counter-strike-2': '/images/games/cs2.svg',
  'garrys-mod': '/images/games/gmod.svg',
  'gmod': '/images/games/gmod.svg',
  'ark': '/images/games/ark.svg',
  'ark-survival': '/images/games/ark.svg',
  'palworld': '/images/games/palworld.svg',
  'valheim': '/images/games/valheim.svg',
  'satisfactory': '/images/games/satisfactory.svg'
};

export default function GameImage({ src, alt, className, loading = 'lazy', gameSlug, onError }: GameImageProps) {
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  // Prioritize external URLs, fallback to local images if needed
  const getImageSrc = () => {
    // If there's an error with the original src, use fallback logic
    if (imageError) {
      // Try to find a local image based on gameSlug
      if (gameSlug && LOCAL_GAME_IMAGES[gameSlug]) {
        return LOCAL_GAME_IMAGES[gameSlug];
      }

      // Try to find local image based on alt text
      const foundLocalImage = Object.entries(LOCAL_GAME_IMAGES).find(([slug]) => 
        alt.toLowerCase().includes(slug) || alt.toLowerCase().includes(slug.replace('-', ' '))
      );
      if (foundLocalImage) {
        return foundLocalImage[1];
      }

      // Final fallback
      return '/images/games/minecraft.svg';
    }

    // If original src is a placeholder API, try to find local image
    if (src.includes('/api/placeholder')) {
      if (gameSlug && LOCAL_GAME_IMAGES[gameSlug]) {
        return LOCAL_GAME_IMAGES[gameSlug];
      }

      const foundLocalImage = Object.entries(LOCAL_GAME_IMAGES).find(([slug]) => 
        alt.toLowerCase().includes(slug) || alt.toLowerCase().includes(slug.replace('-', ' '))
      );
      if (foundLocalImage) {
        return foundLocalImage[1];
      }
    }

    // Use the original src (external URLs will be used as-is)
    return src;
  };

  const handleImageError = (e: React.SyntheticEvent<HTMLImageElement, Event>) => {
    if (!imageError) {
      setImageError(true);
      setImageLoading(false);
    }
    if (onError) {
      onError(e);
    }
  };

  const handleImageLoad = () => {
    setImageLoading(false);
  };

  return (
    <div className="relative">
      {imageLoading && (
        <div className={`absolute inset-0 bg-gaming-black-lighter animate-pulse ${className}`} />
      )}
      <img
        src={getImageSrc()}
        alt={alt}
        className={className}
        loading={loading}
        onError={handleImageError}
        onLoad={handleImageLoad}
        style={{ display: imageLoading ? 'none' : 'block' }}
      />
    </div>
  );
}

server/wisp-integration.ts.content:
import fetch from 'node-fetch';

export interface WispServer {
  id: string;
  name: string;
  game: string;
  status: 'online' | 'offline' | 'starting' | 'stopping';
  ip: string;
  port: number;
  maxPlayers: number;
  currentPlayers: number;
  cpu: number;
  memory: {
    used: number;
    total: number;
  };
  disk: {
    used: number;
    total: number;
  };
  node: string;
  location: string;
  uptime: number;
}

export interface WispClient {
  id: string;
  email: string;
  firstName: string;
  lastName: string;
  servers: WispServer[];
}

export class WispIntegration {
  private apiUrl: string;
  private apiKey: string;

  constructor() {
    this.apiUrl = process.env.WISP_API_URL || 'https://game.voltservers.com';
    this.apiKey = process.env.WISP_API_KEY || '';

    console.log('Wisp integration initialized:');
    console.log('API URL:', this.apiUrl);
    console.log('API Key configured:', !!this.apiKey);
  }

  // Get all servers for a user by email
  async getServersByUserEmail(email: string): Promise<WispServer[]> {
    try {
      if (!this.apiKey || !this.apiUrl) {
        console.log('Missing Wisp credentials for server fetch');
        return [];
      }

      console.log('Fetching servers for user:', email);

      // First get user by email
      const cleanUrl = this.apiUrl.endsWith('/') ? this.apiUrl.slice(0, -1) : this.apiUrl;
      const usersUrl = `${cleanUrl}/api/application/users`;

      const usersResponse = await fetch(usersUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (!usersResponse.ok) {
        console.log('Wisp users fetch failed:', usersResponse.status, usersResponse.statusText);
        return [];
      }

      const usersData = await usersResponse.json();
      const user = usersData?.data?.find((u: any) => u.attributes?.email === email);

      if (!user) {
        console.log('User not found in Wisp:', email);
        return [];
      }

      const userId = user.id;
      console.log('Found user ID', userId, 'for email', email);

      // Now get servers for this user
      const serversUrl = `${cleanUrl}/api/application/servers`;
      const serversResponse = await fetch(serversUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (!serversResponse.ok) {
        console.log('Wisp servers fetch failed:', serversResponse.status, serversResponse.statusText);
        return [];
      }

      const serversData = await serversResponse.json();
      console.log('Wisp API response received:', typeof serversData, 'total servers found:', serversData?.data?.length || 0);

      if (!serversData?.data) {
        return [];
      }

      // Filter servers for this user
      const userServers = serversData.data.filter((server: any) => 
        server.attributes?.user === parseInt(userId)
      );

      console.log('Filtered to', userServers.length, 'servers for user', userId);

      return userServers.map((server: any) => {
        console.log('Transforming server:', server.attributes?.name, 'egg:', server.attributes?.egg, 'nest:', server.attributes?.nest);

        return {
          id: server.attributes?.uuid || server.id,
          name: server.attributes?.name || 'Unknown Server',
          game: this.getGameFromEgg(server.attributes?.egg) || 'Unknown',
          status: this.mapWispStatus(server.attributes?.status),
          ip: server.attributes?.allocation?.ip || '',
          port: server.attributes?.allocation?.port || 0,
          maxPlayers: server.attributes?.limits?.players || 0,
          currentPlayers: 0,
          cpu: 0,
          memory: {
            used: 0,
            total: server.attributes?.limits?.memory || 0
          },
          disk: {
            used: 0,
            total: server.attributes?.limits?.disk || 0
          },
          node: server.attributes?.node?.name || 'Unknown',
          location: server.attributes?.node?.location || 'Unknown',
          uptime: 0
        };
      });

    } catch (error) {
      console.error('Error fetching Wisp servers:', error);
      return [];
    }
  }

  // Map egg ID to game name
  private getGameFromEgg(eggId: number): string {
    const eggMap: Record<number, string> = {
      1: 'Minecraft Java Edition',
      2: 'Minecraft Bedrock',
      3: 'Counter-Strike 2',
      4: 'Rust',
      5: 'Garry\'s Mod',
      6: 'ARK: Survival Evolved',
      7: 'Valheim',
      8: 'Palworld',
      9: 'Satisfactory'
    };

    return eggMap[eggId] || 'Unknown Game';
  }

  // Map Wisp status to our status format
  private mapWispStatus(status: string): 'online' | 'offline' | 'starting' | 'stopping' {
    switch (status?.toLowerCase()) {
      case 'running':
        return 'online';
      case 'stopped':
        return 'offline';
      case 'starting':
        return 'starting';
      case 'stopping':
        return 'stopping';
      default:
        return 'offline';
    }
  }

  // Test Wisp connection and list servers
  async testConnection(): Promise<boolean> {
    try {
      if (!this.apiKey || !this.apiUrl) {
        console.log('Missing Wisp credentials - API Key:', !!this.apiKey, 'API URL:', !!this.apiUrl);
        return false;
      }

      // Clean URL and test basic connection
      const cleanUrl = this.apiUrl.endsWith('/') ? this.apiUrl.slice(0, -1) : this.apiUrl;
      const testUrl = `${cleanUrl}/api/application/servers`;

      console.log('Testing Wisp connection to:', testUrl);
      console.log('Using API Key:', this.apiKey.substring(0, 8) + '...');

      const response = await fetch(testUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      console.log('Wisp connection test response:', response.status, response.statusText);

      if (response.ok) {
        const data = await response.json();
        console.log('Wisp test successful, response type:', typeof data);

        // Log available servers for debugging
        if (data?.data) {
          console.log('Available Wisp servers:');
          data.data.forEach((server: any) => {
            console.log(`- Server ID: ${server.id}, Name: ${server.attributes?.name}, UUID: ${server.attributes?.uuid}`);
          });
        }
        return true;
      } else {
        const errorText = await response.text();
        console.log('Wisp test failed:', errorText);
        return false;
      }
    } catch (error) {
      console.error('Wisp connection test failed:', error);
      return false;
    }
  }

  // Get client servers from Wisp based on user account
  async getClientServers(clientEmail: string): Promise<WispServer[]> {
    try {
      if (!this.apiKey || !this.apiUrl) {
        console.log('No Wisp credentials - returning mock data for demo');
        return this.getMockServers();
      }

      // First, get the user ID from email
      const userId = await this.getUserIdFromEmail(clientEmail);
      if (!userId) {
        console.log(`No Wisp user found for email: ${clientEmail}`);
        return [];
      }

      const cleanUrl = this.apiUrl.endsWith('/') ? this.apiUrl.slice(0, -1) : this.apiUrl;
      const apiUrl = `${cleanUrl}/api/application/servers`;

      console.log(`Fetching servers for user ${userId} (${clientEmail}):`, apiUrl);

      const response = await fetch(apiUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.error(`Wisp API error: ${response.status} ${response.statusText}`);
        const errorText = await response.text();
        console.error('Error details:', errorText);
        return [];
      }

      const data = await response.json();
      console.log('Wisp API response received:', typeof data, 'total servers found:', data.data?.length || 0);

      // Handle different response formats from Wisp API
      let allServers = [];
      if (data.data && Array.isArray(data.data)) {
        allServers = data.data;
      } else if (Array.isArray(data)) {
        allServers = data;
      }

      // Filter servers by user ID
      const userServers = allServers.filter(server => {
        const attrs = server.attributes || server;
        return attrs.user === userId;
      });

      console.log(`Filtered to ${userServers.length} servers for user ${userId}`);

      if (userServers.length === 0) {
        console.log(`No servers found for user ${userId} (${clientEmail})`);
        return [];
      }

      return this.transformWispServers(userServers);
    } catch (error) {
      console.error('Error fetching Wisp servers:', error);
      return [];
    }
  }

  // Get all servers (admin function)
  async getAllServers(): Promise<WispServer[]> {
    try {
      if (!this.apiKey || !this.apiUrl) {
        console.log('No Wisp credentials - returning mock data for demo');
        return this.getMockServers();
      }

      const cleanUrl = this.apiUrl.endsWith('/') ? this.apiUrl.slice(0, -1) : this.apiUrl;
      const apiUrl = `${cleanUrl}/api/application/servers`;

      console.log('Fetching all servers from:', apiUrl);

      const response = await fetch(apiUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.error(`Wisp API error: ${response.status} ${response.statusText}`);
        return this.getMockServers();
      }

      const data = await response.json();
      console.log('All servers response:', typeof data, 'total servers found:', data.data?.length || 0);

      let allServers = [];
      if (data.data && Array.isArray(data.data)) {
        allServers = data.data;
      } else if (Array.isArray(data)) {
        allServers = data;
      }

      return this.transformWispServers(allServers);
    } catch (error) {
      console.error('Error fetching all Wisp servers:', error);
      return this.getMockServers();
    }
  }

  // Get user ID from email address
  private async getUserIdFromEmail(email: string): Promise<number | null> {
    try {
      const cleanUrl = this.apiUrl.endsWith('/') ? this.apiUrl.slice(0, -1) : this.apiUrl;
      const apiUrl = `${cleanUrl}/api/application/users`;

      const response = await fetch(apiUrl, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.error(`Failed to fetch users: ${response.status}`);
        return null;
      }

      const data = await response.json();
      const users = data.data || [];

      const user = users.find((u: any) => {
        const attrs = u.attributes || u;
        return attrs.email === email;
      });

      if (user) {
        const userId = user.attributes?.id || user.id;
        console.log(`Found user ID ${userId} for email ${email}`);
        return userId;
      }

      console.log(`No user found with email: ${email}`);
      return null;
    } catch (error) {
      console.error('Error fetching user by email:', error);
      return null;
    }
  }

  // Get specific server details
  async getServerDetails(serverId: string): Promise<WispServer | null> {
    try {
      if (!this.apiKey) {
        const mockServers = this.getMockServers();
        return mockServers.find(s => s.id === serverId) || null;
      }

      const response = await fetch(`${this.apiUrl}/servers/${serverId}`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Wisp API error: ${response.status}`);
      }

      const data = await response.json();
      return this.transformWispServer(data.server);
    } catch (error) {
      console.error('Error fetching Wisp server details:', error);
      return null;
    }
  }

  // Server actions (start, stop, restart)
  async serverAction(serverId: string, action: 'start' | 'stop' | 'restart'): Promise<boolean> {
    try {
      if (!this.apiKey) {
        console.log(`Mock ${action} action for server ${serverId}`);
        return true;
      }

      const response = await fetch(`${this.apiUrl}/servers/${serverId}/power`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ signal: action })
      });

      return response.ok;
    } catch (error) {
      console.error(`Error performing ${action} on server ${serverId}:`, error);
      return false;
    }
  }

  // Get server console logs
  async getServerLogs(serverId: string, lines: number = 50): Promise<string[]> {
    try {
      if (!this.apiKey) {
        return [
          '[INFO] Server starting...',
          '[INFO] Loading world data...',
          '[INFO] Server ready! Players can now connect.',
          '[CONSOLE] This is demo console output - configure Wisp API for real data'
        ];
      }

      const response = await fetch(`${this.apiUrl}/servers/${serverId}/logs?lines=${lines}`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Wisp API error: ${response.status}`);
      }

      const data = await response.json();
      return data.logs || [];
    } catch (error) {
      console.error('Error fetching server logs:', error);
      return ['Error fetching console logs'];
    }
  }

  // Transform Wisp API response to our format
  private transformWispServers(wispServers: any[]): WispServer[] {
    return wispServers.map(server => this.transformWispServer(server));
  }

  private transformWispServer(server: any): WispServer {
    const attrs = server.attributes || server;
    const environment = attrs.container?.environment || {};
    const limits = attrs.limits || {};

    console.log('Transforming server:', attrs.name, 'egg:', attrs.egg, 'nest:', attrs.nest);

    return {
      id: attrs.uuid || attrs.id?.toString() || 'unknown',
      name: attrs.name || 'Unknown Server',
      game: this.getGameFromEgg(attrs.egg, attrs.nest, environment),
      status: attrs.suspended ? 'offline' : 'online',
      ip: this.getServerIP(attrs.allocation, environment),
      port: this.getServerPort(attrs.allocation, environment),
      maxPlayers: this.getMaxPlayers(environment),
      currentPlayers: Math.floor(Math.random() * 15), // Simulated for demo
      cpu: Math.floor(Math.random() * 60), // Simulated for demo
      memory: {
        used: Math.floor((limits.memory || 1024) * 0.4), // Simulated usage
        total: limits.memory || 1024
      },
      disk: {
        used: Math.floor((limits.disk || 10240) * 0.3), // Simulated usage
        total: limits.disk || 10240
      },
      node: `Node-${attrs.node || 1}`,
      location: environment.P_SERVER_LOCATION || 'Vinthill',
      uptime: Math.floor(Math.random() * 86400) // Simulated uptime
    };
  }

  private getGameFromEgg(egg: number, nest: number, environment: any): string {
    // ARK servers
    if (egg === 41 && nest === 17) return 'ARK: Survival Ascended';
    // Minecraft servers
    if (egg === 1 && nest === 1) return 'Minecraft Java Edition';
    // Fallback based on environment
    if (environment.SRCDS_APPID === '2430930') return 'ARK: Survival Ascended';
    if (environment.SERVER_JARFILE) return 'Minecraft Java Edition';
    return 'Game Server';
  }

  private getServerIP(allocation: any, environment: any): string {
    // For demo purposes, return a generic IP
    // In real implementation, you'd fetch allocation details
    return '192.168.1.100';
  }

  private getServerPort(allocation: any, environment: any): number {
    if (environment.SERVER_PORT) return parseInt(environment.SERVER_PORT) || 25565;
    if (allocation?.port) return allocation.port;
    return 25565;
  }

  private getMaxPlayers(environment: any): number {
    if (environment.MAX_PLAYERS) return parseInt(environment.MAX_PLAYERS) || 20;
    return 20;
  }

  private mapWispStatus(status: string): 'online' | 'offline' | 'starting' | 'stopping' {
    switch (status?.toLowerCase()) {
      case 'running':
      case 'online':
        return 'online';
      case 'starting':
        return 'starting';
      case 'stopping':
        return 'stopping';
      default:
        return 'offline';
    }
  }

  // Mock data for demo when no API key is configured
  private getMockServers(): WispServer[] {
    return [
      {
        id: 'srv-minecraft-001',
        name: 'SkyBlock Paradise',
        game: 'Minecraft Java Edition',
        status: 'online',
        ip: '192.168.1.100',
        port: 25565,
        maxPlayers: 50,
        currentPlayers: 23,
        cpu: 45.2,
        memory: { used: 3072, total: 4096 },
        disk: { used: 5120, total: 10240 },
        node: 'Node-US-East-1',
        location: 'Virginia, USA',
        uptime: 172800
      },
      {
        id: 'srv-minecraft-002',
        name: 'Creative Build Server',
        game: 'Minecraft Java Edition',
        status: 'online',
        ip: '192.168.1.101',
        port: 25566,
        maxPlayers: 30,
        currentPlayers: 8,
        cpu: 22.8,
        memory: { used: 2048, total: 4096 },
        disk: { used: 3584, total: 10240 },
        node: 'Node-US-West-1',
        location: 'California, USA',
        uptime: 86400
      },
      {
        id: 'srv-rust-001',
        name: 'Rust PvP Arena',
        game: 'Rust',
        status: 'offline',
        ip: '192.168.1.102',
        port: 28015,
        maxPlayers: 100,
        currentPlayers: 0,
        cpu: 0,
        memory: { used: 0, total: 8192 },
        disk: { used: 7680, total: 20480 },
        node: 'Node-EU-1',
        location: 'Frankfurt, Germany',
        uptime: 0
      }
    ];
  }
}

export const wispIntegration = new WispIntegration();

