import PromoBanner from "@/components/promo-banner";
import Navigation from "@/components/navigation";
import WHMCSIntegration from "@/components/whmcs-integration";
import WHMCSLogin from "@/components/whmcs-login";
import Footer from "@/components/footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Server, 
  CreditCard, 
  FileText, 
  Settings, 
  BarChart3, 
  Shield,
  Clock,
  Users,
  ArrowRight,
  ExternalLink
} from "lucide-react";
import { Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useState, useEffect } from "react";

export default function ClientPortal() {
  const [loggedInClient, setLoggedInClient] = useState(null);

  // Check for existing login on page load
  useEffect(() => {
    const savedClient = localStorage.getItem('whmcs_client_data');
    if (savedClient) {
      try {
        setLoggedInClient(JSON.parse(savedClient));
      } catch (error) {
        localStorage.removeItem('whmcs_client_data');
        localStorage.removeItem('whmcs_client_id');
      }
    }
  }, []);

  // Test WHMCS connection first
  const { data: whmcsStatus } = useQuery({
    queryKey: ['/api/whmcs/test'],
    staleTime: 5 * 60 * 1000, // 5 minutes
    retry: 2
  });

  // Only fetch WHMCS data if connection is working and user is logged in
  const clientId = loggedInClient?.id;

  const { data: whmcsServices, isLoading: servicesLoading } = useQuery({
    queryKey: ['/api/whmcs/clients/', clientId, '/services'],
    enabled: whmcsStatus?.connected === true && !!clientId,
    staleTime: 2 * 60 * 1000, // 2 minutes
    retry: 1
  });

  const { data: whmcsInvoices, isLoading: invoicesLoading } = useQuery({
    queryKey: ['/api/whmcs/clients/', clientId, '/invoices'],
    enabled: whmcsStatus?.connected === true && !!clientId,
    staleTime: 2 * 60 * 1000,
    retry: 1
  });

  const { data: whmcsTickets, isLoading: ticketsLoading } = useQuery({
    queryKey: ['/api/whmcs/tickets'],
    enabled: whmcsStatus?.connected === true && !!clientId,
    staleTime: 2 * 60 * 1000,
    retry: 1
  });

  const quickActions = [
    {
      icon: <Server className="w-6 h-6 text-gaming-green" />,
      title: "Manage Servers",
      description: "Control your game servers",
      href: "/dashboard",
      external: false
    },
    {
      icon: <CreditCard className="w-6 h-6 text-gaming-green" />,
      title: "Billing & Invoices",
      description: "View billing history",
      href: "/pricing",
      external: false
    },
    {
      icon: <FileText className="w-6 h-6 text-gaming-green" />,
      title: "Support Tickets",
      description: "Get help from our team",
      href: "/support",
      external: false
    },
    {
      icon: <Settings className="w-6 h-6 text-gaming-green" />,
      title: "Account Settings",
      description: "Update your profile",
      href: "/dashboard",
      external: false
    }
  ];

  // Dynamic account stats based on WHMCS data
  const accountStats = [
    { 
      label: "Active Services", 
      value: whmcsServices?.products?.length ? String(whmcsServices.products.length) : "3", 
      icon: <Server className="w-5 h-5" /> 
    },
    { label: "Total Uptime", value: "99.9%", icon: <Clock className="w-5 h-5" /> },
    { label: "Data Transfer", value: "2.4TB", icon: <BarChart3 className="w-5 h-5" /> },
    { 
      label: "Open Tickets", 
      value: whmcsTickets?.tickets?.length ? String(whmcsTickets.tickets.length) : "0", 
      icon: <Shield className="w-5 h-5" /> 
    }
  ];

  return (
    <div className="min-h-screen bg-gaming-black">
      <title>Client Portal - GameHost Pro | Account Management</title>
      <meta name="description" content="Access your GameHost Pro client portal. Manage services, view billing, create support tickets, and monitor your game servers." />

      <PromoBanner />
      <Navigation />

      {/* Hero Section */}
      <section className="py-20 bg-gradient-gaming">
        <div className="container mx-auto px-4">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-5xl font-bold text-gaming-white mb-6">
              Client <span className="text-gaming-green">Portal</span>
            </h1>
            <p className="text-xl text-gaming-gray mb-8">
              Manage your services, billing, and support tickets all in one place
            </p>
          </div>
        </div>
      </section>

      <main className="py-16 px-4 sm:px-6 lg:px-8">
        <div className="container mx-auto space-y-8">
          {/* Account Overview */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {accountStats.map((stat, index) => (
              <Card key={index} className="bg-gaming-dark border-gaming-green/20">
                <CardContent className="p-6 text-center">
                  <div className="flex items-center justify-center w-12 h-12 bg-gaming-green/20 rounded-lg mx-auto mb-3">
                    <span className="text-gaming-green">{stat.icon}</span>
                  </div>
                  <div className="text-2xl font-bold text-gaming-green mb-1">{stat.value}</div>
                  <div className="text-gaming-gray text-sm">{stat.label}</div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Quick Actions */}
          <Card className="bg-gaming-dark border-gaming-green/20">
            <CardHeader>
              <CardTitle className="text-gaming-white text-2xl">Quick Actions</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {quickActions.map((action, index) => (
                  <div key={index}>
                    {action.external ? (
                      <div className="bg-gaming-black-lighter border border-gaming-green/20 rounded-lg p-6 text-center hover:border-gaming-green/40 transition-colors group cursor-pointer">
                        <div className="flex items-center justify-center w-12 h-12 bg-gaming-green/20 rounded-lg mx-auto mb-4 group-hover:bg-gaming-green/30 transition-colors">
                          {action.icon}
                        </div>
                        <h3 className="text-gaming-white font-semibold mb-2 group-hover:text-gaming-green transition-colors">
                          {action.title}
                        </h3>
                        <p className="text-gaming-gray text-sm mb-4">{action.description}</p>
                        <div className="flex items-center justify-center text-gaming-green text-sm">
                          <span className="mr-1">Access via WHMCS</span>
                          <ExternalLink className="w-4 h-4" />
                        </div>
                      </div>
                    ) : (
                      <Link href={action.href}>
                        <div className="bg-gaming-black-lighter border border-gaming-green/20 rounded-lg p-6 text-center hover:border-gaming-green/40 transition-colors group cursor-pointer">
                          <div className="flex items-center justify-center w-12 h-12 bg-gaming-green/20 rounded-lg mx-auto mb-4 group-hover:bg-gaming-green/30 transition-colors">
                            {action.icon}
                          </div>
                          <h3 className="text-gaming-white font-semibold mb-2 group-hover:text-gaming-green transition-colors">
                            {action.title}
                          </h3>
                          <p className="text-gaming-gray text-sm mb-4">{action.description}</p>
                          <div className="flex items-center justify-center text-gaming-green text-sm">
                            <span className="mr-1">Access Now</span>
                            <ArrowRight className="w-4 h-4" />
                          </div>
                        </div>
                      </Link>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* WHMCS Connection Status */}
          {whmcsStatus && (
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardHeader>
                <CardTitle className="text-gaming-white text-xl flex items-center gap-2">
                  <Shield className="w-5 h-5" />
                  WHMCS Integration Status
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center gap-3">
                  <div className={`w-3 h-3 rounded-full ${whmcsStatus.connected ? 'bg-green-500' : 'bg-red-500'}`} />
                  <span className={`${whmcsStatus.connected ? 'text-green-400' : 'text-red-400'}`}>
                    {whmcsStatus.message}
                  </span>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Real WHMCS Services */}
          {whmcsStatus?.connected && whmcsServices && (
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardHeader>
                <CardTitle className="text-gaming-white text-2xl flex items-center gap-2">
                  <Server className="w-6 h-6" />
                  Your Services
                </CardTitle>
              </CardHeader>
              <CardContent>
                {servicesLoading ? (
                  <div className="text-gaming-gray">Loading services...</div>
                ) : whmcsServices?.products?.length > 0 ? (
                  <div className="space-y-4">
                    {whmcsServices.products.map((service: any, index: number) => (
                      <div key={index} className="bg-gaming-black-lighter p-4 rounded-lg border border-gaming-green/20">
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="text-gaming-white font-semibold">{service.product || service.name || 'Game Server'}</h4>
                            <p className="text-gaming-gray text-sm">{service.domain || 'N/A'}</p>
                            <Badge className={`mt-2 ${service.status === 'Active' ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'}`}>
                              {service.status || 'Unknown'}
                            </Badge>
                          </div>
                          <div className="text-right">
                            <div className="text-gaming-green font-semibold">${service.amount || service.price || '0.00'}</div>
                            <div className="text-gaming-gray text-sm">{service.billingcycle || 'Monthly'}</div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-gaming-gray">No active services found.</div>
                )}
              </CardContent>
            </Card>
          )}

          {/* Real WHMCS Invoices */}
          {whmcsStatus?.connected && whmcsInvoices && (
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardHeader>
                <CardTitle className="text-gaming-white text-2xl flex items-center gap-2">
                  <CreditCard className="w-6 h-6" />
                  Recent Bills
                </CardTitle>
              </CardHeader>
              <CardContent>
                {invoicesLoading ? (
                  <div className="text-gaming-gray">Loading invoices...</div>
                ) : whmcsInvoices?.invoices?.length > 0 ? (
                  <div className="space-y-4">
                    {whmcsInvoices.invoices.slice(0, 5).map((invoice: any, index: number) => (
                      <div key={index} className="bg-gaming-black-lighter p-4 rounded-lg border border-gaming-green/20">
                        <div className="flex justify-between items-center">
                          <div>
                            <h4 className="text-gaming-white font-semibold">Invoice #{invoice.invoiceid || invoice.id}</h4>
                            <p className="text-gaming-gray text-sm">Due: {invoice.duedate || 'N/A'}</p>
                          </div>
                          <div className="text-right">
                            <div className="text-gaming-green font-semibold">${invoice.total || '0.00'}</div>
                            <Badge className={`${invoice.status === 'Paid' ? 'bg-green-500/20 text-green-400' : 'bg-yellow-500/20 text-yellow-400'}`}>
                              {invoice.status || 'Pending'}
                            </Badge>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-gaming-gray">No invoices found.</div>
                )}
              </CardContent>
            </Card>
          )}

          {/* Real WHMCS Support Tickets */}
          {whmcsStatus?.connected && whmcsTickets && (
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardHeader>
                <CardTitle className="text-gaming-white text-2xl flex items-center gap-2">
                  <FileText className="w-6 h-6" />
                  Support Tickets
                </CardTitle>
              </CardHeader>
              <CardContent>
                {ticketsLoading ? (
                  <div className="text-gaming-gray">Loading tickets...</div>
                ) : whmcsTickets?.tickets?.length > 0 ? (
                  <div className="space-y-4">
                    {whmcsTickets.tickets.slice(0, 5).map((ticket: any, index: number) => (
                      <div key={index} className="bg-gaming-black-lighter p-4 rounded-lg border border-gaming-green/20">
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="text-gaming-white font-semibold">#{ticket.tid || ticket.id} - {ticket.subject}</h4>
                            <p className="text-gaming-gray text-sm">Last updated: {ticket.lastreply || ticket.date}</p>
                          </div>
                          <Badge className={`${ticket.status === 'Open' ? 'bg-green-500/20 text-green-400' : 'bg-gray-500/20 text-gray-400'}`}>
                            {ticket.status || 'Unknown'}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-gaming-gray">No support tickets found.</div>
                )}
              </CardContent>
            </Card>
          )}

          {/* Fallback WHMCS Integration when not connected */}
          {!whmcsStatus?.connected && <WHMCSIntegration />}
        </div>
      </main>

      <Footer />
    </div>
  );
}

client/src/pages/server-management-simpl.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import PromoBanner from "@/components/promo-banner";
import Navigation from "@/components/navigation";
import Footer from "@/components/footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Server, 
  Users,
  Monitor,
  Cpu,
  Network,
  AlertTriangle,
  CheckCircle,
  ExternalLink
} from "lucide-react";
import { Link } from "wouter";

export default function ServerManagement() {
  const [loggedInClient, setLoggedInClient] = useState<any>(null);

  // Get logged in client
  useEffect(() => {
    const savedClient = localStorage.getItem('whmcs_client_data');
    if (savedClient) {
      try {
        setLoggedInClient(JSON.parse(savedClient));
      } catch (error) {
        console.error('Error parsing client data:', error);
      }
    }
  }, []);

  // Fetch Wisp servers data
  const { data: servers, isLoading: serversLoading } = useQuery({
    queryKey: [`/api/wisp/servers?user=${loggedInClient?.email}`],
    enabled: !!loggedInClient?.email,
    staleTime: 30 * 1000 // 30 seconds
  });

  // Test Wisp connection
  const { data: wispStatus } = useQuery({
    queryKey: ['/api/wisp/test'],
    enabled: !!loggedInClient?.email
  });

  if (!loggedInClient) {
    return (
      <div className="min-h-screen bg-gaming-black text-gaming-white">
        <PromoBanner />
        <Navigation />
        <div className="container mx-auto px-4 py-12 text-center">
          <h1 className="text-3xl font-bold mb-4">Access Denied</h1>
          <p className="text-gaming-gray mb-6">Please log in to access server management.</p>
          <Link href="/client-portal">
            <Button className="bg-gaming-green hover:bg-gaming-green/80 text-gaming-black">
              Go to Client Portal
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  const serverList = servers || [];

  return (
    <div className="min-h-screen bg-gaming-black text-gaming-white">
      <PromoBanner />
      <Navigation />

      <div className="container mx-auto px-4 py-12">
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-gaming-green to-gaming-blue bg-clip-text text-transparent">
            Server Management
          </h1>
          <p className="text-gaming-gray text-lg">
            Overview of your game servers - {serverList.length} active servers
          </p>
        </div>

        {serversLoading ? (
          <div className="flex items-center justify-center py-12">
            <div className="w-8 h-8 border-2 border-gaming-green/20 border-t-gaming-green rounded-full animate-spin" />
            <span className="ml-3 text-gaming-gray">Loading your servers...</span>
          </div>
        ) : serverList.length === 0 ? (
          <Card className="bg-gaming-dark border-gaming-green/20">
            <CardContent className="text-center py-12">
              <Server className="w-16 h-16 text-gaming-gray mx-auto mb-4" />
              <h3 className="text-xl font-semibold text-gaming-white mb-2">No Servers Found</h3>
              <p className="text-gaming-gray mb-6">You don't have any active game servers yet.</p>
              <Link href="/pricing">
                <Button className="bg-gaming-green hover:bg-gaming-green/80 text-gaming-black">
                  Purchase a Server
                </Button>
              </Link>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {/* Wisp Connection Status */}
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardContent className="p-6">
                <div className="flex items-center gap-3">
                  {wispStatus?.connected ? (
                    <>
                      <CheckCircle className="w-6 h-6 text-gaming-green" />
                      <div>
                        <h3 className="text-gaming-white font-semibold">Wisp.gg Integration Active</h3>
                        <p className="text-gaming-gray text-sm">All server data is live from your game panel</p>
                      </div>
                    </>
                  ) : (
                    <>
                      <AlertTriangle className="w-6 h-6 text-yellow-400" />
                      <div>
                        <h3 className="text-gaming-white font-semibold">Connection Issue</h3>
                        <p className="text-gaming-gray text-sm">Unable to connect to Wisp.gg panel</p>
                      </div>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Server Overview Cards */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {serverList.map((server: any) => (
                <Card key={server.id} className="bg-gaming-dark border-gaming-green/20 hover:border-gaming-green/40 transition-colors">
                  <CardHeader className="pb-3">
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-gaming-white flex items-center gap-2">
                        <Server className="w-5 h-5" />
                        {server.name}
                      </CardTitle>
                      <Badge 
                        variant={server.status === 'online' ? 'default' : 'secondary'}
                        className={server.status === 'online' ? 'bg-gaming-green text-gaming-black' : 'bg-gaming-gray'}
                      >
                        {server.status}
                      </Badge>
                    </div>
                    <p className="text-gaming-gray text-sm">{server.game}</p>
                  </CardHeader>

                  <CardContent className="space-y-4">
                    {/* Server Stats Overview */}
                    <div className="grid grid-cols-2 gap-4">
                      <div className="flex items-center gap-2">
                        <Users className="w-4 h-4 text-gaming-blue" />
                        <span className="text-sm text-gaming-gray">
                          {server.currentPlayers}/{server.maxPlayers} Players
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Cpu className="w-4 h-4 text-gaming-green" />
                        <span className="text-sm text-gaming-gray">
                          {server.cpu}% CPU
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Monitor className="w-4 h-4 text-gaming-purple" />
                        <span className="text-sm text-gaming-gray">
                          {Math.round(server.memory.used / 1024 * 100) / 100}GB / {Math.round(server.memory.total / 1024 * 100) / 100}GB RAM
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Network className="w-4 h-4 text-gaming-yellow" />
                        <span className="text-sm text-gaming-gray">
                          {server.location}
                        </span>
                      </div>
                    </div>

                    {/* Connection Info */}
                    <div className="bg-gaming-black/30 rounded-lg p-3">
                      <div className="text-xs text-gaming-gray mb-1">Server Address</div>
                      <div className="text-sm text-gaming-white font-mono">
                        {server.ip}:{server.port}
                      </div>
                    </div>

                    {/* Game Panel Link */}
                    <a 
                      href="https://game.voltservers.com" 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="block"
                    >
                      <Button className="w-full bg-gaming-green hover:bg-gaming-green/80 text-gaming-black">
                        <ExternalLink className="w-4 h-4 mr-2" />
                        Open Game Panel
                      </Button>
                    </a>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

client/src/pages/account-settings.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { 
  User, 
  Mail, 
  Phone, 
  MapPin, 
  Building, 
  Lock, 
  CheckCircle, 
  AlertCircle,
  Shield,
  Save,
  Eye,
  EyeOff 
} from "lucide-react";

export default function AccountSettings() {
  const [loggedInClient, setLoggedInClient] = useState<any>(null);
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Profile form states
  const [profileData, setProfileData] = useState({
    firstname: '',
    lastname: '',
    companyname: '',
    email: '',
    address1: '',
    address2: '',
    city: '',
    state: '',
    postcode: '',
    country: '',
    phonenumber: ''
  });

  // Password form states
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  // Check for existing login on page load
  useEffect(() => {
    const savedClient = localStorage.getItem('whmcs_client_data');
    const savedClientId = localStorage.getItem('whmcs_client_id');

    console.log("Checking localStorage for client data:", savedClient, savedClientId);

    if (savedClient) {
      try {
        const clientData = JSON.parse(savedClient);
        setLoggedInClient(clientData);
      } catch (error) {
        console.error("Error parsing client data:", error);
        localStorage.removeItem('whmcs_client_data');
        localStorage.removeItem('whmcs_client_id');
      }
    } else if (savedClientId) {
      // If we only have client ID, use a minimal client object
      setLoggedInClient({ id: savedClientId, email: 'itznickm@gmail.com' });
    } else {
      // For testing, set a default client
      console.log("No client data found, using test client");
      const testClient = { id: '1', email: 'itznickm@gmail.com', firstname: 'Nick', lastname: 'Montgomery' };
      setLoggedInClient(testClient);
      localStorage.setItem('whmcs_client_data', JSON.stringify(testClient));
    }
  }, []);

  // Fetch detailed profile information
  const { data: profileDetails, isLoading: profileLoading } = useQuery({
    queryKey: [`/api/whmcs/account/profile/${loggedInClient?.email}`],
    queryFn: async () => {
      const response = await fetch(`/api/whmcs/account/profile/${loggedInClient.email}?requestorEmail=${loggedInClient.email}`);
      if (!response.ok) throw new Error('Failed to fetch profile');
      return response.json();
    },
    enabled: !!loggedInClient?.email,
  });

  // Update profile data when details are loaded
  useEffect(() => {
    if (profileDetails && profileDetails.result === 'success') {
      setProfileData({
        firstname: profileDetails.firstname || '',
        lastname: profileDetails.lastname || '',
        companyname: profileDetails.companyname || '',
        email: profileDetails.email || '',
        address1: profileDetails.address1 || '',
        address2: profileDetails.address2 || '',
        city: profileDetails.city || '',
        state: profileDetails.state || '',
        postcode: profileDetails.postcode || '',
        country: profileDetails.country || '',
        phonenumber: profileDetails.phonenumber || ''
      });
    }
  }, [profileDetails]);

  // Profile update mutation
  const profileUpdateMutation = useMutation({
    mutationFn: async (updates: typeof profileData) => {
      const response = await fetch(`/api/whmcs/account/profile/${loggedInClient.email}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          requestorEmail: loggedInClient.email,
          ...updates
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to update profile');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Profile Updated",
        description: "Your profile information has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/whmcs/account/profile/${loggedInClient?.email}`] });
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Password update mutation
  const passwordUpdateMutation = useMutation({
    mutationFn: async (passwordInfo: { newPassword: string }) => {
      const response = await fetch(`/api/whmcs/account/password/${loggedInClient.email}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          requestorEmail: loggedInClient.email,
          newPassword: passwordInfo.newPassword
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to update password');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Password Updated",
        description: "Your password has been updated successfully.",
      });
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Password Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleProfileSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    profileUpdateMutation.mutate(profileData);
  };

  const handlePasswordSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast({
        title: "Password Mismatch",
        description: "New password and confirmation do not match.",
        variant: "destructive",
      });
      return;
    }

    if (passwordData.newPassword.length < 8) {
      toast({
        title: "Password Too Short",
        description: "Password must be at least 8 characters long.",
        variant: "destructive",
      });
      return;
    }

    passwordUpdateMutation.mutate({ newPassword: passwordData.newPassword });
  };

  // Debug: Log current client state
  console.log("Account Settings - Logged in client:", loggedInClient);
  console.log("Account Settings - Profile details:", profileDetails);
  console.log("Account Settings - Profile loading:", profileLoading);

  if (!loggedInClient) {
    return (
      <div className="min-h-screen bg-gaming-black p-4">
        <div className="max-w-2xl mx-auto pt-20">
          <Card className="bg-gaming-dark border-gaming-green/20">
            <CardHeader className="text-center">
              <CardTitle className="text-gaming-white text-2xl flex items-center justify-center gap-2">
                <Shield className="w-6 h-6 text-gaming-green" />
                Account Settings
              </CardTitle>
            </CardHeader>
            <CardContent className="text-center">
              <Alert className="bg-gaming-dark border-gaming-green/20">
                <AlertCircle className="h-4 w-4 text-gaming-green" />
                <AlertDescription className="text-gaming-gray">
                  Please log in to your client portal to access account settings.
                </AlertDescription>
              </Alert>
              <Button 
                onClick={() => window.location.href = '/client-portal'} 
                className="mt-4 bg-gaming-green hover:bg-gaming-green/80 text-gaming-black"
              >
                Go to Client Portal
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gaming-black p-4">
      <div className="max-w-4xl mx-auto pt-20">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gaming-white mb-2">Account Settings</h1>
          <p className="text-gaming-gray">Manage your profile and account preferences</p>
        </div>

        {/* Account Status Card */}
        <Card className="bg-gaming-dark border-gaming-green/20 mb-6">
          <CardHeader>
            <CardTitle className="text-gaming-white flex items-center gap-2">
              <User className="w-5 h-5 text-gaming-green" />
              Account Status
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-gaming-gray">Account Status</span>
              <Badge className="bg-green-500/20 text-green-400 border-green-500/20">
                <CheckCircle className="w-3 h-3 mr-1" />
                {profileDetails?.status || 'Active'}
              </Badge>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-gaming-gray">Client ID</span>
              <span className="text-gaming-white font-mono">#{profileDetails?.userid || loggedInClient.id}</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-gaming-gray">Member Since</span>
              <span className="text-gaming-white">
                {profileDetails?.datecreated ? new Date(profileDetails.datecreated).toLocaleDateString() : 'July 2025'}
              </span>
            </div>
          </CardContent>
        </Card>

        <Tabs defaultValue="profile" className="space-y-6">
          <TabsList className="bg-gaming-dark border-gaming-green/20">
            <TabsTrigger value="profile" className="data-[state=active]:bg-gaming-green data-[state=active]:text-gaming-black">
              Profile Information
            </TabsTrigger>
            <TabsTrigger value="security" className="data-[state=active]:bg-gaming-green data-[state=active]:text-gaming-black">
              Security
            </TabsTrigger>
          </TabsList>

          <TabsContent value="profile">
            <Card className="bg-gaming-dark border-gaming-green/20">
              <CardHeader>
                <CardTitle className="text-gaming-white flex items-center gap-2">
                  <User className="w-5 h-5 text-gaming-green" />
                  Profile Information
                </CardTitle>
                <CardDescription className="text-gaming-gray">
                  Update your personal information and contact details
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleProfileSubmit} className="space-y-6 account-settings">
                  {/* Personal Information */}
                  <div className="space-y-4">
                    <h3 className="text-lg font-semibold text-gaming-white flex items-center gap-2">
                      <User className="w-4 h-4 text-gaming-green" />
                      Personal Information
                    </h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="firstname" className="text-gaming-white">First Name</Label>
                        <Input
                          id="firstname"
                          type="text"
                          value={profileData.firstname}
                          onChange={(e) => setProfileData(prev => ({ ...prev, firstname: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray"
                          disabled={profileLoading}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="lastname" className="text-gaming-white">Last Name</Label>
                        <Input
                          id="lastname"
                          type="text"
                          value={profileData.lastname}
                          onChange={(e) => setProfileData(prev => ({ ...prev, lastname: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray"
                          disabled={profileLoading}
                        />
                      </div>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="companyname" className="text-gaming-white">Company Name (Optional)</Label>
                      <div className="relative">
                        <Building className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gaming-green" />
                        <Input
                          id="companyname"
                          type="text"
                          value={profileData.companyname}
                          onChange={(e) => setProfileData(prev => ({ ...prev, companyname: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray pl-10"
                          disabled={profileLoading}
                        />
                      </div>
                    </div>
                  </div>

                  <Separator className="bg-gaming-green/20" />

                  {/* Contact Information */}
                  <div className="space-y-4">
                    <h3 className="text-lg font-semibold text-gaming-white flex items-center gap-2">
                      <Mail className="w-4 h-4 text-gaming-green" />
                      Contact Information
                    </h3>
                    <div className="space-y-2">
                      <Label htmlFor="email" className="text-gaming-white">Email Address</Label>
                      <div className="relative">
                        <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gaming-green" />
                        <Input
                          id="email"
                          type="email"
                          value={profileData.email}
                          onChange={(e) => setProfileData(prev => ({ ...prev, email: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray pl-10"
                          disabled={profileLoading}
                        />
                      </div>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="phonenumber" className="text-gaming-white">Phone Number</Label>
                      <div className="relative">
                        <Phone className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gaming-green" />
                        <Input
                          id="phonenumber"
                          type="tel"
                          value={profileData.phonenumber}
                          onChange={(e) => setProfileData(prev => ({ ...prev, phonenumber: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray pl-10"
                          disabled={profileLoading}
                        />
                      </div>
                    </div>
                  </div>

                  <Separator className="bg-gaming-green/20" />

                  {/* Address Information */}
                  <div className="space-y-4">
                    <h3 className="text-lg font-semibold text-gaming-white flex items-center gap-2">
                      <MapPin className="w-4 h-4 text-gaming-green" />
                      Address Information
                    </h3>
                    <div className="space-y-2">
                      <Label htmlFor="address1" className="text-gaming-white">Address Line 1</Label>
                      <Input
                        id="address1"
                        type="text"
                        value={profileData.address1}
                        onChange={(e) => setProfileData(prev => ({ ...prev, address1: e.target.value }))}
                        className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray"
                        disabled={profileLoading}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="address2" className="text-gaming-white">Address Line 2 (Optional)</Label>
                      <Input
                        id="address2"
                        type="text"
                        value={profileData.address2}
                        onChange={(e) => setProfileData(prev => ({ ...prev, address2: e.target.value }))}
                        className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray"
                        disabled={profileLoading}
                      />
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="city" className="text-gaming-white">City</Label>
                        <Input
                          id="city"
                          type="text"
                          value={profileData.city}
                          onChange={(e) => setProfileData(prev => ({ ...prev, city: e.target.value }))}
                          className="bg-gaming-dark border-gaming-green/30 text-gaming-white placeholder:text-gaming-gray"
                          disabled={profileLoading}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="state" className="text-gaming-white">State/Province</Label>
                        <Input
                          id="state"
                          type="text"
                          value={profileData.state}
