        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...faqItem,
          tags: faqItem.tags.split(',').map((tag: string) => tag.trim()).filter(Boolean)
        })
      });

      if (!response.ok) throw new Error('Failed to save FAQ item');
      return response.json();
